package com.loops;

public class DoWhileExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int index=1;

do
{
System.out.println(index+""+"Testing");	
index++;
}
while(index<=3);


System.out.println();
System.out.println("**************");
System.out.println();


index=1;
do 
{
	System.out.println(index+""+"Testing");
	index++;
}
while(index>=3);



	


	}

}
